                    ),
                ),
            ),
        ),
    ),
    'controller' => array(
        'classes' => array(
            '<module-name>-Index' => '<module-namespace>\Controller\IndexController',
            // Do similar for each other controller in your module
        ),
    ),
    // ... other configuration ...
);

